INSERT INTO science (id, cw, prompt, answer, note) VALUES (1, 3, 'mass', 'the amount of matter (substance) in an object, usually measured in grams (g) or pound-mass (lbm)', NULL);
INSERT INTO science (id, cw, prompt, answer, note) VALUES (2, 4, 'weight', 'the force of gravity on a mass, usually measured in Newtons (N) or pound-force (lbf)', 'A given mass will weigh *more* on earth than on the moon, since the earth''s gravitaional force is greater than, e.g., that of the moon.');
INSERT INTO science (id, cw, prompt, answer, note) VALUES (3, 1, 'atom', 'the smallest unit of matter of a chemical element', 'The Periodic Table of Elements details chemical elements.');
INSERT INTO science (id, cw, prompt, answer, note) VALUES (4, 2, 'molecule', 'matter that consists of two or more atoms held together by chemical bonds', NULL);
INSERT INTO science (id, cw, prompt, answer, note) VALUES (5, 5, 'chemical solution', 'a chemical mixture; a solvent is the substance in which a solute is dissolved', 'Mixtures are commonly (but not necessarily) liquid.');
INSERT INTO science (id, cw, prompt, answer, note) VALUES (6, 6, 'water cycle', 'the cycle in which water enters the atmosphere thorough evaporation and transpiration, forms clouds through condensation, and falls as rain and snow, called precipitation', 'The water cycle is also known as the hydrologic cycle.');
INSERT INTO science (id, cw, prompt, answer, note) VALUES (7, 7, 'atmospheric layers', 'troposphere, stratosphere, mesosphere, thermosphere, and exosphere', 'The troposphere, stratosphere, and mesosphere are grouped into "the homosphere", and the thermosphere and exosphere are grouped into "the heterosphere".');
INSERT INTO science (id, cw, prompt, answer, note) VALUES (8, 8, 'layers of the earth', 'core, mantle, and crust', 'The core is broken into the "inner core" and "outer core"; the mantle includes the asthenosphere, and the crust includes the lithosphere.');
INSERT INTO science (id, cw, prompt, answer, note) VALUES (9, 9, 'kinds of rock', 'sedimentary, igneous, and metamorphic', 'Rocks are made up of minerals, and minerals are crystalline structures made of distinctive (atomic) elements.');
INSERT INTO science (id, cw, prompt, answer, note) VALUES (10, 10, 'types of clouds', 'cumulus, cirrus, stratus, lenticular', 'Clouds form in the homosphere (troposphere, stratosphere, and mesosphere).  Using "alto-" and "-nimbus" modifiers, as well as combinations (like "cirrocumulus") are common approaches to further classifying clouds. These are all Latin terms: Cumulus = "heap", "pile" ("accumulate"). Cirrus = "ringlet", "curling lock of hair". Stratus = "layer" ("strategy"). Lenticular = "lens-shaped" or "lentil-shaped". Nimbus = "dark cloud".');
INSERT INTO science (id, cw, prompt, answer, note) VALUES (11, 11, 'velocity', 'the speed and direction of an object in motion; speed is usually measured in meters per second', 'Miles per hour and many other units can apply here. A "vector" is a quantity that has both magnitude and direction components, such as velocity.');
INSERT INTO science (id, cw, prompt, answer, note) VALUES (12, 12, 'acceleration', 'the rate of change in velocity, usually measured in meters per second per second', 'Meters per second per second can also be written meters per second-squared, but meters per second per second is easier to visualize.  If a car is accelerating at 3 m/s/s, then it is increasing in speed by 3 m/s every second ("per second").');
INSERT INTO science (id, cw, prompt, answer, note) VALUES (13, 13, 'force', 'an interaction which can change the velocity of an object; force = mass x acceleration (F = ma), usually measured in Newtons', NULL);
INSERT INTO science (id, cw, prompt, answer, note) VALUES (14, 14, 'momentum', 'the product of an object''s mass and its velocity (p = mv), usually measured in kilogram-meters per second', 'Momentum = mass times velocity.');
