INSERT INTO detail (id, title, detail, url, sequence) VALUES (62, 4, '”Following the decline and rupture of the Assyrian empire, Babylon assumed supremacy in the region from 605-549 BCE. Babylon then fell to the Persians under Cyrus the Great who founded the Achaemenid Empire (549-330 BCE) which fell to Alexander the Great and, after his death, was part of the Seleucid Empire.” – ancient.eu', NULL, NULL);
INSERT INTO detail (id, title, detail, url, sequence) VALUES (63, 4, 'The Hyksos were likely Semitic, from Syria/Palestine, but are pretty mysterious in the historical record.  Note that the Nubians (Kush) were pushing on Egypt from the South (Upper Egypt) when the Hyksos were taking over the North (Lower Egypt).', NULL, NULL);
