INSERT INTO science (id, cw, run, prompt_prefix, prompt, answer_prefix, answer_verb, answer, note, qr_key, lighter_bg) VALUES (1, 11, NULL, 'is', 'mass', '', 'is', 'the amount of matter (substance) in an object, usually measured in grams (g) or pound-mass (lbm)', NULL, 257, NULL);
INSERT INTO science (id, cw, run, prompt_prefix, prompt, answer_prefix, answer_verb, answer, note, qr_key, lighter_bg) VALUES (2, 12, NULL, 'is', 'weight', '', 'is', 'the force of gravity on a mass, usually measured in Newtons (N) or pound-force (lbf)', 'A given mass will weigh *more* on earth than on the moon, since the earth''s gravitaional force is greater than, e.g., that of the moon.', 258, 1);
INSERT INTO science (id, cw, run, prompt_prefix, prompt, answer_prefix, answer_verb, answer, note, qr_key, lighter_bg) VALUES (3, 1, NULL, 'is an', 'atom', 'an', 'is', 'the smallest unit of matter of a chemical element, consisting of protons, neutrons, and electrons', 'Atoms consist of protons, neutrons, and electrons.  The Periodic Table of Elements details chemical elements.', 259, NULL);
INSERT INTO science (id, cw, run, prompt_prefix, prompt, answer_prefix, answer_verb, answer, note, qr_key, lighter_bg) VALUES (4, 2, NULL, 'is a', 'molecule', 'a', 'is', 'matter that consists of two or more atoms held together by chemical bonds', NULL, 260, 1);
INSERT INTO science (id, cw, run, prompt_prefix, prompt, answer_prefix, answer_verb, answer, note, qr_key, lighter_bg) VALUES (5, 57, NULL, 'is a', 'chemical solution', 'a', 'is', 'a chemical mixture; a solvent is the substance in which a solute is dissolved', 'Mixtures are commonly (but not necessarily) liquid.', 261, NULL);
INSERT INTO science (id, cw, run, prompt_prefix, prompt, answer_prefix, answer_verb, answer, note, qr_key, lighter_bg) VALUES (6, 6, NULL, 'is the', 'water cycle', 'the', 'is', 'the cycle in which water enters the atmosphere through evaporation and transpiration, forms clouds through condensation, and falls as rain and snow, called precipitation', 'The water cycle is also known as the hydrologic cycle.', 262, 1);
INSERT INTO science (id, cw, run, prompt_prefix, prompt, answer_prefix, answer_verb, answer, note, qr_key, lighter_bg) VALUES (7, 3, NULL, 'are the', 'atmospheric layers', 'the', 'are:', 'troposphere, stratosphere, mesosphere, thermosphere, and exosphere', 'The troposphere, stratosphere, and mesosphere are grouped into "the homosphere", and the thermosphere and exosphere are grouped into "the heterosphere".', 263, 1);
INSERT INTO science (id, cw, run, prompt_prefix, prompt, answer_prefix, answer_verb, answer, note, qr_key, lighter_bg) VALUES (8, 8, NULL, 'are the', 'layers of the earth', 'the', 'are:', 'inner core, outer core, mantle, and crust', 'The mantle includes a layer called the asthenosphere, and the crust includes a layer called the lithosphere.', 264, NULL);
INSERT INTO science (id, cw, run, prompt_prefix, prompt, answer_prefix, answer_verb, answer, note, qr_key, lighter_bg) VALUES (9, 9, NULL, 'are', 'some kinds of rock', '', 'are:', 'sedimentary, igneous, and metamorphic', 'Rocks are made up of minerals, and minerals are crystalline structures made of distinctive (atomic) elements.', 265, NULL);
INSERT INTO science (id, cw, run, prompt_prefix, prompt, answer_prefix, answer_verb, answer, note, qr_key, lighter_bg) VALUES (10, 7, NULL, 'are', 'some types of clouds', '', 'are:', 'cumulus, cirrus, stratus, and lenticular', 'Clouds form in the homosphere (troposphere, stratosphere, and mesosphere).  Using "alto-" and "-nimbus" modifiers, as well as combinations (like "cirrocumulus") are common approaches to further classifying clouds. These are all Latin terms: Cumulus = "heap", "pile" ("accumulate"). Cirrus = "ringlet", "curling lock of hair". Stratus = "layer" ("strategy"). Lenticular = "lens-shaped" or "lentil-shaped". Nimbus = "dark cloud".', 266, NULL);
INSERT INTO science (id, cw, run, prompt_prefix, prompt, answer_prefix, answer_verb, answer, note, qr_key, lighter_bg) VALUES (11, 13, 2, 'is', 'velocity', '', 'is', 'the speed and direction of an object in motion, usually measured in meters per second', 'miles per hour and many other units can apply here. A "vector" is a quantity that has both magnitude and direction components, such as velocity.', 267, NULL);
INSERT INTO science (id, cw, run, prompt_prefix, prompt, answer_prefix, answer_verb, answer, note, qr_key, lighter_bg) VALUES (12, 14, 2, 'is', 'acceleration', '', 'is', 'the rate of change in velocity, usually measured in meters per second per second', 'meters per second per second can also be written meters per second-squared, but meters per second per second is easier to visualize.  If a car is accelerating at 3 m/s/s, then it is increasing in speed by 3 m/s every second ("per second").', 268, NULL);
INSERT INTO science (id, cw, run, prompt_prefix, prompt, answer_prefix, answer_verb, answer, note, qr_key, lighter_bg) VALUES (13, 15, 2, 'is', 'force', '', 'is', 'an interaction which can change the velocity of an object; force = mass x acceleration (F = ma), usually measured in Newtons', NULL, 269, NULL);
INSERT INTO science (id, cw, run, prompt_prefix, prompt, answer_prefix, answer_verb, answer, note, qr_key, lighter_bg) VALUES (14, 28, NULL, 'is', 'momentum', '', 'is', 'the product of an object''s mass and its velocity (p = mv), usually measured in kilogram-meters per second', 'momentum = mass times velocity', 270, NULL);
INSERT INTO science (id, cw, run, prompt_prefix, prompt, answer_prefix, answer_verb, answer, note, qr_key, lighter_bg) VALUES (15, 4, NULL, 'is a', 'chemical reaction', 'a', 'is', 'the rearrangement of molecular structure, usually involving an exchange of atoms in the reactants that results in transformed substances', NULL, 271, 1);
INSERT INTO science (id, cw, run, prompt_prefix, prompt, answer_prefix, answer_verb, answer, note, qr_key, lighter_bg) VALUES (16, 5, NULL, 'are the', 'states of matter', 'the', 'are:', 'solid, liquid, gas, and plasma', 'Consider the Mpemba Effect, which states that water that starts hotter may freeze faster than cooler water.', 272, NULL);
INSERT INTO science (id, cw, run, prompt_prefix, prompt, answer_prefix, answer_verb, answer, note, qr_key, lighter_bg) VALUES (17, 10, 2, 'are', 'some common minerals', '', 'are:', 'quartz, feldspar, copper, cobalt, talc, and pyrite', NULL, 273, 1);
INSERT INTO science (id, cw, run, prompt_prefix, prompt, answer_prefix, answer_verb, answer, note, qr_key, lighter_bg) VALUES (18, 16, 2, 'is', 'Newton''s First Law', '', 'states that', 'an object in motion will remain in motion until it is acted upon by an outside force', 'An object at rest will remain at rest until it is acted upon by an outside force; rest is just "motion = 0".', 366, NULL);
INSERT INTO science (id, cw, run, prompt_prefix, prompt, answer_prefix, answer_verb, answer, note, qr_key, lighter_bg) VALUES (19, 17, 2, 'is', 'Newton''s Second Law', '', 'states that', 'the acceleration of an object caused by a force is proportional to the force and inversely proportional to the mass of the object (i.e. F = ma)', NULL, 367, NULL);
INSERT INTO science (id, cw, run, prompt_prefix, prompt, answer_prefix, answer_verb, answer, note, qr_key, lighter_bg) VALUES (20, 18, 2, 'is', 'Newton''s Third Law', '', 'states that', 'for every action, there is an equal and opposite reaction', NULL, 368, NULL);
INSERT INTO science (id, cw, run, prompt_prefix, prompt, answer_prefix, answer_verb, answer, note, qr_key, lighter_bg) VALUES (21, 19, 2, 'is', 'centripetal force', '', 'is', 'the force needed to keep an object moving in a circular path, directed toward the center of that path', 'That is, the force that constantly changes the directional component of the objects''s path "toward" the center of the circle.  Without this force, the object would continue in a straight path, until acted on by a force, according to Newton''s First Law.  Centripetal force is the force acting on the object, changing its direction constantly ("pulling" it toward the center of the circle).', 369, NULL);
INSERT INTO science (id, cw, run, prompt_prefix, prompt, answer_prefix, answer_verb, answer, note, qr_key, lighter_bg) VALUES (22, 20, 2, 'is', 'voltage', '', 'is', 'the force that drives an electrical current, measured in volts', 'Voltage is the difference in electrical potential between two points; the higher this difference, the greater the potential energey, and thus the greater the electromotive force to drive electrical charges between the points, if there is a pathway to do so.', 370, NULL);
